mapper.insert:
insert into cash_apply (id, user_id, create_time, 
  money, status, pay_time
  )
values (#{id}, #{userId}, now(), 
  #{money}, #{status}, null
  )

mapper.update:
update cash_apply
set user_id = #{userId},
  money = #{money},
  status = #{status}
where id = #{id}

mapper.select:
select id, user_id, create_time, money, status, pay_time
from cash_apply
where id = #{id}

select id, user_id, create_time, money, status, pay_time
from cash_apply
where 1=1 

        AND user_id = #{userId}


        AND status = #{status}

limit #{start},#{pageSize}
