mapper.update:
update mote_task
set 
  accepted_time = now(),
  update_time = now(),
  status = #{status}
where id = #{id}

update mote_task
set 
  update_time = now(),
  status = #{param2}
where id = #{param1}

mapper.select:
select *
from mote_task
where id = #{id}

select t.id,t.create_time,t.title, t.number as total_num, sum(case when mt.status >=1 then 1 else 0 end) as accpeted_num, sum(case when mt.status >=2 then 1 else 0 end) as ordered_num, sum(case when mt.status >=3 then 1 else 0 end) as finish_show_num, sum(case when mt.status >=4 then 1 else 0 end) as upload_img_num, sum(case when mt.status =5 then 1 else 0 end) as self_buy_num, sum(case when mt.status =6 then 1 else 0 end) as return_num, sum(case when mt.status =7 then 1 else 0 end) as kefu_num, sum(case when mt.finish_status =1 then 1 else 0 end) as finished_num from task t left join mote_task mt on (mt.task_id=t.id) where t.status=#{status} and t.id in
#{item}
group by t.id,t.create_time,t.title,total_num

	    select mt.id,u.nickname,mt.order_no,mt.status,mt.finish_status from mote_task mt,user u
        where mt.user_id=u.id and mt.task_id=#{taskId}
        limit #{start},#{pageSize}

	    select ifnull(count(1),0)
         from mote_task mt,user u
        where mt.user_id=u.id and mt.task_id=#{param1}

	    select mt.id,t.title,t.price,t.shot_fee,t.self_buy_off,mt.status,mt.create_time
        from mote_task mt,task t
        where t.id=mt.task_id and mt.user_id=#{moteId}
        order by mt.id desc
        limit #{start},#{pageSize}

select ifnull(sum(number),0)
from task t
where t.user_id=#{param1}

select distinct mt.task_id
from mote_task mt
where mt.user_id=#{param1}

select *
from mote_task mt
where user_id=#{param1} and task_id = #{param2}

select mt.id,t.user_id,t.title,t.url,t.price,t.shot_fee,t.img_url,t.self_buy_off from mote_task mt,task t where mt.task_id=t.id and mt.user_id=#{userId}
AND mt.status = 0
AND mt.status in (1,2,3,4,5,6)
AND mt.finish_status = 1
AND mt.status = 7
order by mt.id desc limit #{start},#{pageSize}
