mapper.insert:
insert into reduce_cash_apply ( user_id, create_time, 
  money, status
  )
values (#{userId}, now(), 
  #{money}, #{status}
  )

mapper.update:
update reduce_cash_apply
set user_id = #{userId},
  money = #{money},
  status = #{status}
where id = #{id}

mapper.select:
select id, user_id, create_time, money, status, pay_time
from reduce_cash_apply
where id = #{id}

select id, user_id, create_time, money, status, pay_time
from reduce_cash_apply
where 1=1 

        AND user_id = #{userId}


        AND status = #{status}

order by id desc
limit #{start},#{pageSize}

select ifnull(count(1),0)
from reduce_cash_apply
where 1=1 

        AND user_id = #{userId}


        AND status = #{status}
