mapper.insert:
insert into trade_flow (refer_id, type, 
  money, create_time, user_id
  )
values (#{referId}, #{type}, 
  #{money}, now(), #{userId}
  )

mapper.update:
update trade_flow
set refer_id = #{referId},
  type = #{type},
  money = #{money},
  user_id = #{userId}
where id = #{id}

mapper.select:
select id, refer_id, type, money, create_time, user_id
from trade_flow
where id = #{id}
