mapper.update:
update audio_upload
set 
  name = #{name}
where id = #{id}

mapper.select:
select id, question_id, duration, name, url, subject, status, approve_time, teacher_id, 
create_time, update_time, type, source,feud_type,wb_type
from audio_upload 
where  type=1 and source=4 and teacher_id = #{teacherId} and id >#{cuurent}
order by id desc
limit #{pageSize}
