mapper.update:
update feud_detail_wb
set feud_detail_id = #{feudDetailId},
  file_url = #{fileUrl},file_type = #{fileType},wb_id = #{wbId}
  update_time = now()
where id = #{id}

mapper.select:
select id, feud_detail_id, file_url, create_time, update_time,file_type,wb_id,wb_version
from feud_detail_wb
where id = #{id}
