mapper.insert:
insert into feud_point_fee_conf (id, star_evaluate, teacher_identify, 
  value, conf_type, description
  )
values (#{id}, #{starEvaluate}, #{teacherIdentify}, 
  #{value}, #{confType}, #{description}
  )

mapper.select:
select id, star_evaluate, teacher_identify, value, conf_type, description
from feud_point_fee_conf
where id = #{id}
